import { useEffect, useRef, useState } from "react";
import '../css/globals.css';
import Header from './header';
import Footer from './footer';
import '../css/study-game.css';
// import '../logic/study-game-logic.js';

const problems = [
    {
        "problem": "기업의 경제 상태를 나타내는 주요 지표로, 매출, 이익율, 부채 등이 포함되는 것은?",
        "answer": "펀더멘탈",
    },
    {
        "problem": "기업의 총 발행 주식수에 1주당 주가를 곱한 값으로, 시장에서 기업의 규모를 나타내는 것은?",
        "answer": "시가총액",
    },
    // 나머지 문제들은 생략...
];

const Game = () => {
    const [timeElapsed, setTimeElapsed] = useState(0);
    const [timer, setTimer] = useState(null);
    const [questionArray, setQuestionArray] = useState([]);
    const [answerArray, setAnswerArray] = useState([]);
    const [round, setRound] = useState(1);
    const [score, setScore] = useState(0);

    useEffect(() => {
        const totalTime = 30;
        const timer = setInterval(() => {
            if (timeElapsed >= totalTime) {
                clearInterval(timer);
                alert(`타이머가 종료되었습니다. 점수는 ${score}입니다.`);
            } else {
                setTimeElapsed(prev => prev + 1);
            }
        }, 1000);

        setTimer(timer);

        return () => clearInterval(timer);
    }, [timeElapsed, score]);

    const gameAnswerAppear = () => {
        if (timeElapsed >= 30) {
            clearInterval(timer);
            return;
        }

        setRound(prev => prev + 1);

        let rand1;
        do {
            rand1 = Math.floor(Math.random() * problems.length);
        } while (questionArray.includes(rand1));

        setQuestionArray(prev => [...prev, rand1]);

        document.querySelector(".question").innerHTML = problems[rand1].problem;

        const randomAnswerIndex = Math.floor(Math.random() * 4) + 1;
        const randomAnswerElement = document.querySelector(`.answer.answer${randomAnswerIndex}`);
        randomAnswerElement.innerText = problems[rand1].answer;

        setAnswerArray(prev => [...prev, randomAnswerIndex]);

        const usedProblems = [rand1];
        for (let i = 1; i <= 4; i++) {
            if (i !== randomAnswerIndex) {
                let randomNotAnswerIndex;
                do {
                    randomNotAnswerIndex = Math.floor(Math.random() * problems.length);
                } while (usedProblems.includes(randomNotAnswerIndex));
                usedProblems.push(randomNotAnswerIndex);
                document.querySelector(`.answer.answer${i}`).innerText = problems[randomNotAnswerIndex].answer;
            }
        }
    };

    const handleAnswerClick = (clickedButtonIndex) => {
        if (clickedButtonIndex === answerArray[answerArray.length - 1]) {
            setScore(prev => prev + 10);
        }
        gameAnswerAppear();
    };
    
    return (
        <div className="rect">
            <div className="question_number">Q</div>
            <div className="question"></div>
            
            <div className="answer answer1">답1</div>
            <div className="answer answer2">답2</div>
            <div className="answer answer3">답3</div>
            <div className="answer answer4">답4</div>

            <div className="nextSection">
                <div className="next">다음</div>
            </div>

            <div id="timeBar">
                <div id="timer" style={timeBarStyle}>{count}</div>
            </div>
        </div>
    );
}

function App() {
    return (
        <div>
            <Header />
            <Main />
            <Footer />
        </div>
    );
}

export default App;
