import React, { useState } from 'react';
import '../../css/virtual/part2.css';

function Part2() {
    const [isTableShown, setIsTableShown] = useState(true);

    const toggleNextVisibility = () => {
        setIsTableShown(prevVisibility => !prevVisibility);
    };

    return (
        <section className="part" id="part2">
            <div className="graph"></div>
            <div className="button-line">
                <button onClick={() => toggleNextVisibility(true)}>표</button>
                <button onClick={() => toggleNextVisibility(false)}>뉴스</button>
            </div>
            <div className={isTableShown ? 'table' : 'news'}></div>
        </section>
    );
}

export default Part2;
