import { useEffect, useRef, useState } from "react";
import '../css/globals.css';
import Header from './header';
import Footer from './footer';
import '../css/study-game.css';
// import '../logic/study-game-logic.js';

const problems = [
    {
      problem: "기업의 경제 상태를 나타내는 주요 지표로, 매출, 이익율, 부채 등이 포함되는 것은?",
      answer: "펀더멘탈"
    },
    {
      problem: "기업의 총 발행 주식수에 1주당 주가를 곱한 값으로, 시장에서 기업의 규모를 나타내는 것은?",
      answer: "시가총액"
    },
    {
      problem: "기업이 투자자나 이해관계자에게 사업내용, 재무상황 등을 알리는 제도는?",
      answer: "공시"
    },
    {
      problem: "개인 투자자가 PC로 주식 거래를 할 수 있는 프로그램을 말하는 것은?",
      answer: "HTS(홈 트레이딩 시스템)"
    },
    {
      problem: "주식을 사는 것을 의미하는 것은?",
      answer: "매수"
    },
    {
      problem: "가지고 있는 주식을 파는 것을 의미하는 것은?",
      answer: "매도"
    },
    {
      problem: "기업이 이익의 일부를 주주에게 돌려주는 것을 의미하는 것은?",
      answer: "배당"
    },
    {
      problem: "주식시장이 열릴 때의 주가를 의미하는 것은?",
      answer: "시가"
    },
    {
      problem: "주식시장이 닫힐 때의 주가를 의미하는 것은?",
      answer: "종가"
    },
    {
      problem: "기업의 순이익을 순자산에 대한 비율로 나타내는 것은?",
      answer: "ROE(자기자본이익율)"
    },
    {
      problem: "주당순이익을 의미하는 것은?",
      answer: "EPS"
    },
    {
      problem: "주가수익률로, 현재 주가와 주당순이익을 비교하여 사용되는 것은",
      answer: "PER"
    },
    {
      problem: "주당순자산으로, 회사를 청산했을 때 주주에게 분배될 순자산을 나타내는 것은?",
      answer: "PBR"
    }
  ];
  

const useInterval = (callback, delay) => {
    const savedCallback = useRef(callback);

    useEffect(() => {
        savedCallback.current = callback;
    });

    useEffect(() => {
        if (delay === null) return;

        const timer = setInterval(() => savedCallback.current(), delay);
        return () => clearInterval(timer);
    }, [delay]);
}


const gameAnswerAppear = () => {
    if (timeElapsed >= totalTime) return;

    let rand1;
    do {
      rand1 = Math.floor(Math.random() * problems.length);
    } while (questionArray.includes(rand1));
    
    setQuestionArray(prevQuestionArray => [...prevQuestionArray, rand1]);
    setRound(prevRound => prevRound + 1);
    setQuestion(problems[rand1].problem);
    
    const randomAnswerIndex = Math.floor(Math.random() * 4) + 1;
    const shuffledAnswers = shuffleAnswers(rand1, randomAnswerIndex);
    setAnswerArray(shuffledAnswers);
  };

  const shuffleAnswers = (correctAnswerIndex, randomAnswerIndex) => {
    const answers = [];
    const usedProblems = [correctAnswerIndex];
    
    for (let i = 1; i <= 4; i++) {
      if (i === randomAnswerIndex) {
        answers.push(problems[correctAnswerIndex].answer);
      } else {
        let randomNotAnswerIndex;
        do {
          randomNotAnswerIndex = Math.floor(Math.random() * problems.length);
        } while (usedProblems.includes(randomNotAnswerIndex));
        
        usedProblems.push(randomNotAnswerIndex);
        answers.push(problems[randomNotAnswerIndex].answer);
      }
    }
    return answers;
  };

  const handleAnswerClick = (clickedButtonIndex) => {
    if (clickedButtonIndex === answerArray.length) {
      console.log("정답입니다!");
      setScore(prevScore => prevScore + 10);
    } else {
      console.log("틀렸습니다!");
    }
    gameAnswerAppear();
  };


function Main() {
    const [count, setCount] = useState(30); // 60초부터 시작하도록 변경
    const [round, setRound] = useState(1);
    const [score, setScore] = useState(0);
    const [question, setQuestion] = useState("");
    const [answerArray, setAnswerArray] = useState([]);
    const [questionArray, setQuestionArray] = useState([]);

    
    useInterval(() => {
        setCount(prevCount => {
            if (prevCount === 0) {
                return 0; // 카운트가 0이면 그대로 반환하여 더 이상 감소하지 않도록 설정
            } else {
                return prevCount - 1; // 0이 아니면 1씩 감소
            }
        });
    }, 1000); // 1초마다 호출하도록 수정

    const timeBarStyle = {
        height: `${100 - (count / 30) * 100}%`,
        transition: "height 1s linear"
    };


    return (
        <div className="rect">
            <div className="question_number">Q</div>
            <div className="question"></div>
            
            <div className="answer answer1">답1</div>
            <div className="answer answer2">답2</div>
            <div className="answer answer3">답3</div>
            <div className="answer answer4">답4</div>

            <div className="nextSection">
                <div className="next">다음</div>
            </div>

            <div id="timeBar">
                <div id="timer" style={timeBarStyle}></div>
            </div>
        </div>
    );
}

function App() {
    return (
        <div>
            <Header />
            <Main />
            <Footer />
        </div>
    );
}

export default App;
