import React, { useState } from 'react';
import '../css/style.css';
import Header from './header';
import '../css/study-virtual.css';

function App() {
    const [nextHidden, setNextHidden] = useState(true);
    const [line_table, setLine_table] = useState(flase);
    const [line_view, setLine_view] = useState(true);

    const toggleNextHidden = () => {
        setNextHidden(!nextHidden);
    };
    const toggleTableHidden = () => {
        setNext_table(!line_table);
    };
    const toggleViewHidden = () => {
        setLine_view(!line_view);
    };

    return (
        <div>

            <Header />

            <main>
                <section className="part" id="part1">
                    <div className="logo"></div>
                    <div className="step">
                        <button onClick={toggleNextHidden()}></button>
                    </div>
                    <div className="info"></div>
                </section>

                <section className="part" id="part2">
                    <div className="graph"></div>
                    <div className="button-line">
                        <button onClick={toggleTableHidden()}></button>
                        <button onClick={toggleViewHidden()}></button>
                    </div>
                    <div className="view" id="table"></div>
                    {/* <div className="view hidden" id="{isHidden ? 'hidden' : 'news'}"></div> */}
                </section>

                <section className="part" id="part3">
                    <div className="interact"></div>
                    <div className="margin"></div>
                    <div className="next"></div>
                </section>
            </main>

            <div className="detail" id={nextHidden ? 'hidden' : ''}>
                <div className="paper">
                    <div className='title'>결과화면</div>
                    <div className='result'></div>
                    <div className='next'>
                        <button id='next-btn' onClick={toggleHidden(nextHidden)}>다음화면</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
export default App;
