import { useEffect, useRef, useState } from "react";
import '../css/globals.css';
import Header from './header';
import Footer from './footer';
import '../css/study-game.css';
// import '../logic/study-game-logic.js';

const useInterval = (callback, delay) => {
    const savedCallback = useRef(callback);

    useEffect(() => {
        savedCallback.current = callback;
    });

    useEffect(() => {
        if (delay === null) return;

        const timer = setInterval(() => savedCallback.current(), delay);
        return () => clearInterval(timer);
    }, [delay]);
}


const updateProgressBar = () => {
    const progress = (timeElapsed / totalTime) * 100;
    return { height: `${progress}%` };
  };

function Main() {
    const [count, setCount] = useState(30); // 60초부터 시작하도록 변경
    useInterval(() => {
        setCount(prevCount => {
            if (prevCount === 0) {
                return 0; // 카운트가 0이면 그대로 반환하여 더 이상 감소하지 않도록 설정
            } else {
                return prevCount - 1; // 0이 아니면 1씩 감소
            }
        });
    }, 1000); // 1초마다 호출하도록 수정

    return (
        <div class="rect">
            <div class="question_number">Q</div>
            <div class="question"></div>
            
            <div class="answer answer1">답1</div>
            <div class="answer answer2">답2</div>
            <div class="answer answer3">답3</div>
            <div class="answer answer4">답4</div>

            <div class="nextSection">
                <div class="next">다음</div>
            </div>

            <div id="timeBar">
                <div id="timer" style={updateProgressBar()}></div>
            </div>
        </div>
    );
}

function App() {
    return (
        <div>
            <Header />
            <Main />
            <Footer />

        </div>
    );
}

export default App;
